    public static ArrayList<Psis> getListData(){
        ArrayList<Psis> list = new ArrayList<>();
        for (String [] aData : data){
            Psis psis = new Psis();
            psis.setId(Integer.parseInt(aData[0]));
            psis.setName(aData[1]);
            psis.setDescription(aData[2]);
            psis.setPhoto(aData[3]);

            list.add(psis);
        }
        return list;
    }
